CREATE TABLE news (
    id SERIAL PRIMARY KEY,
    title VARCHAR(255),
    content TEXT,
    author VARCHAR(100),
    description VARCHAR(255),
    created_at TIMESTAMP DEFAULT NOW(),
    image BYTEA
);

CREATE TABLE travels (
    t_id SERIAL PRIMARY KEY,
    altText VARCHAR(255),
    title VARCHAR(255),
    content text,
    price integer,
    duration text,
    totalPassengers integer,
    distance integer,
    t_type VARCHAR(255), 
    description text,
    destination text[],
    fromWhere text,
    startDate DATE,
    vehicle text[],
    included text[],
    notIncluded text[],
    t_plans JSONB[],
    created_at TIMESTAMP DEFAULT NOW(),
    image text,
    album text[],
    status VARCHAR(255)
);


CREATE TABLE blogs (
    id SERIAL PRIMARY KEY,
    altText VARCHAR(255),
    title VARCHAR(255),
    content text,
    author VARCHAR(100),
    publishDate DATE,
    category VARCHAR(255),
    image text,
);

CREATE TABLE reviews (
    id SERIAL PRIMARY KEY,
    author VARCHAR(50),
    review text,
    created_at DATE
);

CREATE TABLE bookings (
    id SERIAL PRIMARY KEY,
    lastName VARCHAR(100),
    firstName VARCHAR(100),
    phoneNumber VARCHAR(20),
    mail VARCHAR(100),
    t_type VARCHAR(255),
    travel_id INTEGER,
    numberOfTravelers INTEGER,
    additionalInfo text,
    created_at DATE,
    status VARCHAR(50)
);

CREATE TABLE test (
    test_id SERIAL PRIMARY KEY,
    description VARCHAR(255)
)

SELECT *
	FROM public.travels;

