INSERT INTO travels (t_id, altText, title, price, duration, totalPassengers, distance, t_type, description, destination, fromWhere, startDate, vehicle, included, notIncluded, plans, image, album, status)
VALUES (2, 'Солонгос улс, Сөүл хот', 'Солонгос', 2490000, '5 өдөр / 4 шөнө', 30, 1994, 'special', 'Сөүл хот улсынхаа өрнө умард хэсэгт Кёнги аймгаар хүрээлэгдэн, Инчон хоттой хаяа залган оршдог. Хотын харьяалах талбай 605 км² байна. Засаг захиргааны хуваариар улсын нэгдүгээр дэсийн нутаг болох онцгой хот юм. 10 сая хүн оршин сууж байгаагаараа улсдаа тэргүүлэх бөгөөд дэлхийд 8-р байрт жагсах их хот болно. Сөүлийн эргэн тойронд хотын бөөгнөрөл бий болсон бөгөөд энэхүү Сүдугвонь бүсэд улсын тал хувь буюу 25 сая хүн оршин сууж байна. Сөүл хотын дундуур Хань мөрөн урсдаг бөгөөд эртнээс энд суурин үүсч хөгжжээ. НТӨ 18 онд Пэгжэ улс энэ хавьд төвлөснөөс эхлээд өдгөө хүртэл улсын нийслэл болон хөгжжээ. Сөүлд ЮНЕСКО-гийн дэлхийн өвийн газар болох Чандог ордон, Хуасон цайз, Жунмю сүм, Намхань уулын цайз, Чусоний вангуудын бунхан зэрэг түүхийн дурсгалт газар байна. Сөүл хот Пүхань зэрэг уулсаар хүрээлэгдсэн. Хотыг харах өргөн цэцэрлэгт хүрээлэнтэй. Сөүлийн цамхаг, Тундэмүний загварын ордон, Лотте Уорлд, Сарны гэрлийн солонгот хүрхрээ зэрэг орчин үеийн үзэмж цогцолжээ. Солонгос поп, Солонгос давалгааны гол газар бөгөөд дэлхийд эхний 10-т орох тоо буюу жилд 10 сая гаран жуулчин ирж сонирхдог.', ARRAY['Улаанбаатар',
